mapper.select:
SELECT teacher_no teacherNo, teacher_id teacherId, teacher_name teacherName FROM teacher
teacher_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY teacherNo DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT test_id testId, test_title testTitle, test_date testDate, active
FROM test
WHERE grade=#{grade} AND vision=1;
